import React, { useState, useEffect } from 'react';

const API_URL = 'http://localhost:8080/api/inimigos';

function DefesaDeTorresComponent() {
  const [inimigos, setInimigos] = useState([]);
  const [nome, setNome] = useState('');
  const [vida, setVida] = useState('');
  const [velocidade, setVelocidade] = useState('');
  const [numero_da_Fase, setNumero_da_Fase] = useState('');
  const [almanaque_Cod, setAlmanaque_Cod] = useState('');
  const [editItem, setEditItem] = useState(null);
  const [error, setError] = useState(null);


  const fetchInimigos = async () => {
    try {
      const response = await fetch(API_URL);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setInimigos(data);
      setError(null);
    } catch (e) {
      console.error("Erro ao buscar inimigos:", e);
      setError("Não foi possível carregar os dados.");
    }
  };

  useEffect(() => {
    fetchInimigos();
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!nome || vida === '' || velocidade === '') return;

    const inimigo = {
      nome,
      vida: parseInt(vida, 10),
      velocidade: parseFloat(velocidade),
      numero_da_Fase: numero_da_Fase === '' ? null : parseInt(numero_da_Fase, 10),
      almanaque_Cod: almanaque_Cod === '' ? null : parseInt(almanaque_Cod, 10)
    };

    const method = editItem ? 'PUT' : 'POST';
    const url = editItem
      ? `${API_URL}/${encodeURIComponent(editItem.nome ?? editItem.Nome)}`
      : API_URL;

    try {
      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(inimigo),
      });

      if (response.ok) {
        await fetchInimigos();
        resetForm();
      } else {
        const errText = await response.text();
        console.error("Erro do servidor:", errText);
        setError("Falha ao salvar.");
      }
    } catch (error) {
      console.error("Erro ao salvar:", error);
      setError("Erro de conexão com o servidor.");
    }
  };

  const handleEdit = (inimigo) => {
    setEditItem(inimigo);
    setNome(inimigo.nome ?? inimigo.Nome ?? '');
    setVida(inimigo.vida ?? inimigo.Vida ?? '');
    setVelocidade(inimigo.velocidade ?? inimigo.Velocidade ?? '');
    setNumero_da_Fase(inimigo.numero_da_Fase ?? inimigo.Numero_da_Fase ?? '');
    setAlmanaque_Cod(inimigo.almanaque_Cod ?? inimigo.Almanaque_Cod ?? '');
  };

  const handleDelete = async (nomeParam) => {
    try {
      const response = await fetch(`${API_URL}/${encodeURIComponent(nomeParam)}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        await fetchInimigos();
      } else {
        const errText = await response.text();
        console.error("Erro ao deletar:", errText);
        setError("Falha ao deletar.");
      }
    } catch (error) {
      console.error("Erro ao deletar:", error);
      setError("Erro de conexão com o servidor.");
    }
  };

  const resetForm = () => {
    setEditItem(null);
    setNome('');
    setVida('');
    setVelocidade('');
    setNumero_da_Fase('');
    setAlmanaque_Cod('');
  };

  return (
    <div className="component-container">
      <h2>Gerenciamento de Inimigos</h2>

      {error && <p className="error-message">{error}</p>}

      <form onSubmit={handleSubmit} className="form-container">
        <div className="form-grid">
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            placeholder="Nome"
            required
          />
          <input
            type="number"
            value={vida}
            onChange={(e) => setVida(e.target.value)}
            placeholder="Vida (inteiro)"
            required
          />
          <input
            type="number"
            step="0.01"
            value={velocidade}
            onChange={(e) => setVelocidade(e.target.value)}
            placeholder="Velocidade (float)"
            required
          />
          <input
            type="number"
            value={numero_da_Fase}
            onChange={(e) => setNumero_da_Fase(e.target.value)}
            placeholder="Número da Fase (opcional)"
          />
          <input
            type="number"
            value={almanaque_Cod}
            onChange={(e) => setAlmanaque_Cod(e.target.value)}
            placeholder="Almanaque Cod (opcional)"
          />
          <button type="submit">{editItem ? 'Atualizar' : 'Adicionar'}</button>
          {editItem && (
            <button
              type="button"
              onClick={resetForm}
              className="cancel-button"
            >
              Cancelar
            </button>
          )}
        </div>
      </form>

      <h3>Lista de Inimigos</h3>
      <table className="data-table">
        <thead>
          <tr>
            <th>Nome</th>
            <th>Vida</th>
            <th>Velocidade</th>
            <th>Número da Fase</th>
            <th>Almanaque Cod</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {inimigos.map((inimigo) => {
            const key = `${inimigo.nome ?? inimigo.Nome}`;
            return (
              <tr key={key}>
                <td>{inimigo.nome ?? inimigo.Nome}</td>
                <td>{inimigo.vida ?? inimigo.Vida}</td>
                <td>{inimigo.velocidade ?? inimigo.Velocidade}</td>
                <td>{inimigo.numero_da_Fase ?? inimigo.Numero_da_Fase ?? ''}</td>
                <td>{inimigo.almanaque_Cod ?? inimigo.Almanaque_Cod ?? ''}</td>
                <td className="actions-cell">
                  <button onClick={() => handleEdit(inimigo)}>Alterar</button>
                  <button
                    onClick={() => handleDelete(inimigo.nome ?? inimigo.Nome)}
                    className="delete-button"
                  >
                    Deletar
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default DefesaDeTorresComponent;
