import React, { useState, useEffect } from 'react';

const API_URL = 'http://localhost:8080/api/defesas-torres';

function DefesaDeTorresComponent() {
  const [defesas, setDefesas] = useState([]);
  const [jogo, setJogo] = useState('');
  const [codPessoa, setCodPessoa] = useState('');
  const [editItem, setEditItem] = useState(null);
  const [error, setError] = useState(null);

  // Buscar registros da API
  const fetchDefesas = async () => {
    try {
      const response = await fetch(API_URL);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setDefesas(data);
      setError(null);
    } catch (e) {
      console.error("Erro ao buscar defesas:", e);
      setError("Não foi possível carregar os dados.");
    }
  };

  useEffect(() => {
    fetchDefesas();
  }, []);

  // Enviar formulário (criar ou atualizar)
  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!jogo || !codPessoa) return;

    const defesa = {
      jogo: jogo,
      codPessoa: parseInt(codPessoa, 10)
    };

    const url = editItem ? `${API_URL}/${editItem.jogo}/${editItem.codPessoa}` : API_URL;
    const method = editItem ? 'PUT' : 'POST';

    try {
      setError(null);
      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(defesa),
      });

      if (response.ok) {
        fetchDefesas();
        resetForm();
      } else {
        const errText = await response.text();
        console.error("Falha do servidor:", errText);
        throw new Error('Falha ao salvar. Verifique o console do servidor.');
      }
    } catch (error) {
      console.error("Erro ao salvar defesa:", error);
      setError("Não foi possível salvar os dados.");
    }
  };

  // Edição
  const handleEdit = (defesa) => {
    setEditItem(defesa);
    setJogo(defesa.jogo);
    setCodPessoa(defesa.cod_Pessoa);
  };

  // Exclusão
  const handleDelete = async (jogo, cod_Pessoa) => {
    try {
      setError(null);
      const response = await fetch(`${API_URL}/${jogo}/${codPessoa}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        fetchDefesas();
      } else {
        const errText = await response.text();
        console.error("Falha do servidor:", errText);
        throw new Error('Falha ao deletar. Verifique o console do servidor.');
      }
    } catch (error) {
      console.error("Erro ao deletar defesa:", error);
      setError("Não foi possível deletar o registro.");
    }
  };

  const resetForm = () => {
    setEditItem(null);
    setJogo('');
    setCodPessoa('');
  };

  return (
    <div className="component-container">
      <h2>Gerenciamento das Defesas de Torres Jogadas</h2>

      {error && <p className="error-message">{error}</p>}

      <form onSubmit={handleSubmit} className="form-container">
        <input
          type="text"
          value={jogo}
          onChange={(e) => setJogo(e.target.value)}
          placeholder="Nome do jogo"
          required
        />
        <input
          type="number"
          value={codPessoa}
          onChange={(e) => setCodPessoa(e.target.value)}
          placeholder="Código da pessoa"
          required
        />
        <button type="submit">{editItem ? 'Atualizar' : 'Adicionar'}</button>
        {editItem && (
          <button
            type="button"
            onClick={resetForm}
            className="cancel-button"
          >
            Cancelar
          </button>
        )}
      </form>

      <h3>Lista de Defesas de Torres Jogadas</h3>
      <table className="data-table">
        <thead>
          <tr>
            <th>Jogo</th>
            <th>Código da Pessoa</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {defesas.map((defesa) => (
            <tr key={`${defesa.jogo}-${defesa.cod_Pessoa}`}>
              <td>{defesa.jogo}</td>
              <td>{defesa.codPessoa}</td>
              <td className="actions-cell">
                <button onClick={() => handleEdit(defesa)}>Alterar</button>
                <button
                  onClick={() => handleDelete(defesa.jogo, defesa.codPessoa)}
                  className="delete-button"
                >
                  Deletar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default DefesaDeTorresComponent;
