import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  Title,
  Tooltip,
  Legend
} from "chart.js";

import { Bar, Scatter } from "react-chartjs-2";

// Registrando todos os componentes necessários para Barras e Scatter
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  Title,
  Tooltip,
  Legend
);

const API_URL = "http://localhost:8080/api/inimigos";

const InimigosGraficos = () => {
  const [inimigos, setInimigos] = useState([]);
  const [error, setError] = useState(null);

  const fetchInimigos = async () => {
    try {
      const resp = await axios.get(API_URL);
      setInimigos(resp.data);
    } catch (e) {
      setError("Erro ao buscar dados para gráficos.");
    }
  };

  useEffect(() => {
    fetchInimigos();
  }, []);

  if (error) return <p style={{ color: "#ff6b6b", textAlign: "center" }}>{error}</p>;
  if (!inimigos.length) return <p style={{ color: "#FFF", textAlign: "center" }}>Carregando gráficos...</p>;

  // =========================================================
  // 1. DADOS PARA O HISTOGRAMA (Bar Chart): INIMIGOS POR FASE
  // =========================================================
  
  // Agrupar inimigos por fase
  const fasesMap = inimigos.reduce((acc, curr) => {
    // Trata nulos ou undefined como "Sem Fase" ou ignora (aqui optei por agrupar em "0" ou string)
    const fase = curr.numero_da_Fase ?? curr.Numero_da_Fase ?? "N/A";
    acc[fase] = (acc[fase] || 0) + 1;
    return acc;
  }, {});

  // Ordenar as fases numericamente para o eixo X ficar 1, 2, 3...
  const fasesLabels = Object.keys(fasesMap).sort((a, b) => {
    if (a === "N/A") return 1;
    if (b === "N/A") return -1;
    return parseInt(a) - parseInt(b);
  });
  
  const fasesValores = fasesLabels.map(label => fasesMap[label]);

  const histogramaData = {
    labels: fasesLabels,
    datasets: [
      {
        label: "Quantidade de Inimigos",
        data: fasesValores,
        backgroundColor: "#f4a73cB3", // Cor laranja do tema (com transparência)
        borderColor: "#f4a73c",       // Cor laranja sólida
        borderWidth: 1,
      },
    ],
  };

  // =========================================================
  // 2. DADOS PARA O SCATTER PLOT: VIDA x VELOCIDADE
  // =========================================================
  
  const pontosScatter = inimigos.map((ini) => ({
    x: ini.velocidade ?? ini.Velocidade,
    y: ini.vida ?? ini.Vida
  }));

  // Labels para o tooltip saber quem é quem
  const scatterData = {
    datasets: [
      {
        label: "Inimigos (Vida x Velocidade)",
        data: pontosScatter,
        pointRadius: 6,
        backgroundColor: "rgba(54, 162, 235, 0.6)", // Azul
        borderColor: "rgba(54, 162, 235, 1)",
      }
    ]
  };

  // =========================================================
  // CONFIGURAÇÕES GERAIS (OPÇÕES) DOS GRÁFICOS
  // =========================================================

  const commonOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { labels: { color: "#FFF" } },
      title: {
        display: true,
        color: "#FFF",
        font: { size: 16 },
      }
    },
    scales: {
      x: { 
        ticks: { color: "#FFF" }, 
        grid: { color: "#444" },
        title: { display: true, color: "#ccc" }
      },
      y: { 
        ticks: { color: "#FFF" }, 
        grid: { color: "#444" }, 
        beginAtZero: true,
        title: { display: true, color: "#ccc" }
      }
    }
  };

  return (
    <div className="component-container" style={{ marginTop: "30px" }}>
      <h2 style={{ color: "#f4a73c", textAlign: "center", marginBottom: "30px" }}>
        Estatísticas Visuais dos Inimigos
      </h2>

      <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "space-around", gap: "40px" }}>
        
        {/* GRÁFICO 1: HISTOGRAMA DE FASES */}
        <div style={{ width: "100%", maxWidth: "600px", height: "400px" }}>
          <Bar
            data={histogramaData}
            options={{
              ...commonOptions,
              plugins: {
                ...commonOptions.plugins,
                title: { ...commonOptions.plugins.title, text: "Distribuição de Inimigos por Fase (Histograma)" }
              },
              scales: {
                ...commonOptions.scales,
                x: { ...commonOptions.scales.x, title: { display: true, text: "Número da Fase", color: "#FFF" } },
                y: { ...commonOptions.scales.y, title: { display: true, text: "Quantidade", color: "#FFF" } }
              }
            }}
          />
        </div>

        {/* GRÁFICO 2: SCATTER PLOT */}
        <div style={{ width: "100%", maxWidth: "600px", height: "400px" }}>
          <Scatter
            data={scatterData}
            options={{
              ...commonOptions,
              plugins: {
                ...commonOptions.plugins,
                title: { ...commonOptions.plugins.title, text: "Relação Vida x Velocidade" },
                tooltip: {
                    callbacks: {
                        label: (context) => {
                            // Tenta encontrar o nome do inimigo correspondente ao ponto
                            const index = context.dataIndex;
                            const nome = inimigos[index]?.nome ?? inimigos[index]?.Nome ?? "Desconhecido";
                            return `${nome}: Vel(${context.parsed.x}), Vida(${context.parsed.y})`;
                        }
                    }
                }
              },
              scales: {
                ...commonOptions.scales,
                x: { ...commonOptions.scales.x, title: { display: true, text: "Velocidade", color: "#FFF" } },
                y: { ...commonOptions.scales.y, title: { display: true, text: "Vida", color: "#FFF" } }
              }
            }}
          />
        </div>

      </div>
    </div>
  );
};

export default InimigosGraficos;
