import React, { useEffect, useState } from "react";
import axios from "axios";

// Imports do Chart.js
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend
} from "chart.js";

import { Pie } from "react-chartjs-2";

// Registrar plugins necess치rios
ChartJS.register(ArcElement, Tooltip, Legend);

// URL da API
const API_URL = "http://localhost:8080/api/jogos";

const JogadosGraficos = () => {
  const [dados, setDados] = useState([]);
  const [error, setError] = useState(null);

  const fetchDados = async () => {
    try {
      const response = await axios.get(API_URL);
      setDados(response.data);
    } catch (err) {
      console.error(err);
      setError("Erro ao carregar dados do gr치fico.");
    }
  };

  useEffect(() => {
    fetchDados();
  }, []);

  if (error) {
    return <p>{error}</p>;
  }

  if (dados.length === 0) {
    return <p>Carregando dados...</p>;
  }

  // -------------------------------
  // PROCESSAMENTO DOS DADOS
  // -------------------------------
  // Exemplo da API esperada:
  // [
  //   { nomeJogo: "Minecraft", jogadores: 12 },
  //   { nomeJogo: "Valorant", jogadores: 8 },
  //   ...
  // ]

  const nomes = dados.map(item => item.nomeJogo);
  const qtd = dados.map(item => item.jogadores);

  const data = {
    labels: nomes,
    datasets: [
      {
        label: "Quantidade de Jogadores",
        data: qtd,
        backgroundColor: [
          "rgba(255, 99, 132, 0.5)",
          "rgba(54, 162, 235, 0.5)",
          "rgba(255, 206, 86, 0.5)",
          "rgba(75, 192, 192, 0.5)",
          "rgba(153, 102, 255, 0.5)",
          "rgba(255, 159, 64, 0.5)"
        ],
        borderWidth: 1
      }
    ]
  };

  return (
    <div style={{ width: "500px", margin: "0 auto" }}>
      <h2>Jogos Mais Jogados</h2>
      <Pie data={data} />
    </div>
  );
};

export default JogadosGraficos;
