import React, { useEffect, useState } from "react";
import axios from "axios";

import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend
} from "chart.js";

import { Pie } from "react-chartjs-2";

ChartJS.register(ArcElement, Tooltip, Legend);

const API_URL = "http://localhost:8080/api/jogos"; 
// <<< ajuste aqui se sua rota for outra

const 
JogadosGraficos = () => {
  const [dados, setDados] = useState([]);
  const [error, setError] = useState(null);

  const fetchDados = async () => {
    try {
      const response = await axios.get(API_URL);
      setDados(response.data);
    } catch (err) {
      console.error(err);
      setError("Erro ao carregar dados do gráfico.");
    }
  };

  useEffect(() => {
    fetchDados();
  }, []);

  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!dados.length) return <p>Carregando gráfico...</p>;

  // Agrupar quantidade por jogo
  const agrupado = dados.reduce((acc, item) => {
    const jogo = item.jogo;
    acc[jogo] = (acc[jogo] || 0) + 1;
    return acc;
  }, {});

  const labels = Object.keys(agrupado);
  const valores = Object.values(agrupado);

  const chartData = {
    labels: labels,
    datasets: [
      {
        data: valores,
        backgroundColor: [
          "#FF6384B3",
          "#36A2EBB3",
          "#FFCE56B3",
          "#4BC0C0B3",
          "#9966FFB3",
          "#FF9F40B3",
          "#00C49FB3",
        ],
        borderColor: [
          "#FF6384",
          "#36A2EB",
          "#FFCE56",
          "#4BC0C0",
          "#9966FF",
          "#FF9F40",
          "#00C49F",
        ],
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        labels: { color: "#FFF" }
      },
      title: {
        display: true,
        text: "Quantidade de Pessoas por Jogo",
        color: "#FFF",
        font: { size: 18 }
      }
    }
  };

  return (
    <div style={{ width: "50%", margin: "40px auto" }}>
      <Pie data={chartData} options={chartOptions} />
    </div>
  );
};

export default 
JogadosGraficos;
