import React, { useState } from 'react';

const API_URL = 'http://localhost:8080/api/consultas';

function ConsultasComponent() {
  const [resultados, setResultados] = useState([]);
  const [headers, setHeaders] = useState([]);
  const [titulo, setTitulo] = useState('');
  const [error, setError] = useState('');
  const [cod, setCod] = useState(''); // ðŸ”¹ novo estado para o filtro cod
  const [consultaAtual, setConsultaAtual] = useState('');

  const executarConsulta = async (endpoint, tituloConsulta) => {
    try {
      setResultados([]);
      setHeaders([]);
      setError('');
      setTitulo(`Carregando: ${tituloConsulta}...`);

      // ðŸ”¹ se for a primeira consulta, adiciona o parÃ¢metro cod
      let url = `${API_URL}/${endpoint}`;
      if (endpoint === 'jogadores-pvz-clash' && cod.trim() !== '') {
        url += `?cod=${encodeURIComponent(cod)}`;
      }

      const response = await fetch(url);
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();

      if (data.length > 0) {
        setHeaders(Object.keys(data[0]));
        setResultados(data);
      } else {
        setHeaders(['Resultado']);
        setResultados([{ Resultado: 'A consulta nÃ£o retornou resultados.' }]);
      }
      setTitulo(tituloConsulta);
    } catch (e) {
      console.error("Erro ao executar consulta:", e);
      setError(`Falha ao executar a consulta. Verifique o console.`);
      setTitulo(tituloConsulta);
    }
  };

  return (
    <div className="component-container">
      <h2>Consultas Especiais</h2>

      <div className="consultas-botoes">
        <button
          onClick={() => {
            setConsultaAtual('jogadores-pvz-clash');
            setTitulo('Jogadores de PVZ ou Clash Royale');
          }}
        >
          Consulta 1
        </button>
        <button onClick={() => executarConsulta('contagem-jogos-pessoa', 'Contagem de Jogos por Pessoa')}>Consulta 2</button>
        <button onClick={() => executarConsulta('jogos-de-cada-pessoa', 'Jogos de Cada Pessoa')}>Consulta 3</button>
        <button onClick={() => executarConsulta('proxima-fase', 'SequÃªncia de Fases')}>Consulta 4</button>
        <button onClick={() => executarConsulta('inimigos-fase', 'Inimigos na fase')}>View 1</button>
        <button onClick={() => executarConsulta('resumo-financeiro-jogador', 'Resumo financeiro do jogador')}>View 2</button>
        <button onClick={() => executarConsulta('anti-join', 'Pessoa sem jogo jogado')}>AntiJoin</button>
        <button onClick={() => executarConsulta('full-outer-join', 'FaseInimigo')}>FullOuterJoin</button>
        <button onClick={() => executarConsulta('subconsulta-um', 'MoedasRestantes Acima da mÃ©dia')}>Subconsulta 1</button>
        <button onClick={() => executarConsulta('subconsulta-dois', 'Tempo de Jogo Acima da mÃ©dia')}>Subconsulta 2</button>
      </div>

      {/* ðŸ”¹ Exibe o campo filtro cod apenas para a primeira consulta */}
      {consultaAtual === 'jogadores-pvz-clash' && (
        <div className="filtro-container">
          <h3>Filtrar por cÃ³digo (cod)</h3>
          <input
            type="number"
            placeholder="Digite o cÃ³digo"
            value={cod}
            onChange={(e) => setCod(e.target.value)}
          />
          <button
            onClick={() =>
              executarConsulta('jogadores-pvz-clash', 'Jogadores de PVZ ou Clash Royale')
            }
          >
            Executar
          </button>
        </div>
      )}

      {titulo && <h3>{titulo}</h3>}
      {error && <p className="error-message">{error}</p>}

      {resultados.length > 0 && (
        <div className="tabela-container">
          <table className="data-table">
            <thead>
              <tr>
                {headers.map((header) => (
                  <th key={header}>{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {resultados.map((linha, index) => (
                <tr key={index}>
                  {headers.map((header) => (
                    <td key={header}>
                      {linha[header] === null ? 'N/A' : String(linha[header])}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default ConsultasComponent;
