import React, { useEffect, useState } from "react";
import axios from "axios";

import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend
} from "chart.js";

import { Pie } from "react-chartjs-2";

ChartJS.register(ArcElement, Tooltip, Legend);

const API_URL = "http://localhost:8080/api/pessoas";

const jogadosGraficos = () => {
  const [pessoas, setPessoas] = useState([]);

  useEffect(() => {
    axios.get(API_URL)
      .then((resp) => setPessoas(resp.data))
      .catch(() => console.error("Erro ao carregar dados"));
  }, []);

  if (!pessoas.length) return <p>Carregando...</p>;

  // Agrupamento por jogo
  const jogos = pessoas.reduce((acc, pessoa) => {
    const jogo = pessoa.jogo?.toUpperCase() || "NÃO INFORMADO";
    acc[jogo] = (acc[jogo] || 0) + 1;
    return acc;
  }, {});

  const labels = Object.keys(jogos);
  const valores = Object.values(jogos);

  const data = {
    labels: labels,
    datasets: [
      {
        label: "Jogadores por Jogo",
        data: valores,
        backgroundColor: [
          "#FF6384B3",
          "#36A2EBB3",
          "#FFCE56B3",
          "#4BC0C0B3",
          "#9966FFB3",
          "#FF9F40B3"
        ],
        borderColor: [
          "#FF6384",
          "#36A2EB",
          "#FFCE56",
          "#4BC0C0",
          "#9966FF",
          "#FF9F40"
        ],
        borderWidth: 2,
      },
    ],
  };

  const options = {
    plugins: {
      legend: { labels: { color: "#FFF" } },
      title: {
        display: true,
        text: "Quantidade de Pessoas por Jogo",
        color: "#FFF",
        font: { size: 18 }
      }
    },
    responsive: true,
    maintainAspectRatio: false
  };

  return (
    <div style={{ height: "350px", width: "80%", margin: "20px auto" }}>
      <Pie data={data} options={options} />
    </div>
  );
};

export default jogadosGraficos;
