import React, { useEffect, useState } from "react";
import axios from "axios";

// Chart.js imports
import {
  Chart as ChartJS,
  LinearScale,
  PointElement,
  Tooltip,
  Legend,
  Title
} from "chart.js";

import { Scatter } from "react-chartjs-2";

// Registrar elementos do scatter plot
ChartJS.register(LinearScale, PointElement, Tooltip, Legend, Title);

const API_URL = "http://localhost:8080/api/inimigos";

const InimigosScatterPlot = () => {
  const [inimigos, setInimigos] = useState([]);
  const [error, setError] = useState(null);

  const fetchDados = async () => {
    try {
      const response = await axios.get(API_URL);
      setInimigos(response.data);
    } catch (e) {
      console.error(e);
      setError("Erro ao carregar dados do gráfico.");
    }
  };

  useEffect(() => {
    fetchDados();
  }, []);

  if (error) return <p>{error}</p>;
  if (inimigos.length === 0) return <p>Carregando gráfico...</p>;

  // -----------------------
  // Montar pontos do gráfico
  // -----------------------
  const pontos = inimigos.map((ini) => ({
    x: ini.velocidade ?? ini.Velocidade,
    y: ini.vida ?? ini.Vida
  }));

  const labels = inimigos.map((ini) => ini.nome ?? ini.Nome);

  const data = {
    labels,
    datasets: [
      {
        label: "Vida x Velocidade",
        data: pontos,
        pointRadius: 6,
        backgroundColor: "rgba(54, 162, 235, 0.6)"
      }
    ]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: { position: "top" },
      title: { display: true, text: "ScatterPlot - Vida pela Velocidade" }
    },
    scales: {
      x: {
        title: { display: true, text: "Velocidade" },
        beginAtZero: true
      },
      y: {
        title: { display: true, text: "Vida" },
        beginAtZero: true
      }
    }
  };

  return (
    <div style={{ width: "650px", margin: "0 auto" }}>
      <h2>Gráfico de Dispersão dos Inimigos</h2>
      <Scatter data={data} options={options} />
    </div>
  );
};

export default InimigosScatterPlot;
