import React, { useState } from 'react';

const API_URL = 'http://localhost:8080/api/procedimentos';

function BalancearVidaComponent() {
  const [mensagem, setMensagem] = useState('');
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleBalancear = async () => {
    setError(null);
    setMensagem('');
    setIsLoading(true);

    try {
      const response = await fetch(`${API_URL}/balancear-vida`, {
        method: 'POST',
      });
      
      if (!response.ok) {
        const errorData = await response.text();
        throw new Error(errorData || 'Falha ao executar o balanceamento');
      }

      setMensagem('Balanceamento de vida dos inimigos executado com sucesso!');

    } catch (error) {
      console.error("Erro ao executar balanceamento:", error);
      setError("Não foi possível executar o balanceamento.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="component-container">
      <h2>Procedimento: Balancear Vida ⚡</h2>
      
      {error && <p className="error-message">{error}</p>}
      {mensagem && <p className="success-message">{mensagem}</p>}
      
      <div className="form-container">
        <p>Executa o procedimento `BalancearVidaInimigos` no banco de dados.</p>
        <button 
          onClick={handleBalancear} 
          disabled={isLoading}
          className={isLoading ? 'loading-button' : 'primary-button'}
        >
          {isLoading ? 'Executando...' : 'Executar Balanceamento'}
        </button>
      </div>
    </div>
  );
}

export default BalancearVidaComponent;
